import {createAsyncThunk, createSlice, PayloadAction} from "@reduxjs/toolkit";
import axios from "axios";
import {RootState} from "../store";
import {CartItem} from "./cartSlice";

type Pizza = {
    id: number,
    name: string,
    imageUrl: string;
    price: number;
    sizes: number[];
    types: number[];
}

interface PizzaSliceState {
    status: "loading" | "success" | "error";
    items: Pizza[];
}

const initialState: PizzaSliceState = {
    items: [],
    status: 'loading',
}

export const fetchPizzas = createAsyncThunk("pizza/fetchPizzas", async (params: Record<string, string>) => {
    const {currentPage, category, sortBy, order, search} = params;
    const {data} = await axios.get<CartItem[]>(`https://63bbda34cf99234bfa660b9e.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`);

    if (data.length === 0) {
        throw new Error('rejected')
    }
    return data as CartItem[];
})


const pizzaSlice = createSlice({
    name: 'pizza',
    initialState,
    reducers: {
        setItems(state, action) {
            state.items = action.payload;
        }
    },
    extraReducers

    // extraReducers: {
    //     [fetchPizzas.fulfilled]: (state: RootState, action) => {
    //         state.items = action.payload;
    //         state.status = 'success';
    //     },
    //     [fetchPizzas.pending]: (state: RootState, action) => {
    //         state.status = 'loading';
    //         state.items = [];
    //     },
    //     [fetchPizzas.rejected]: (state: RootState, action) => {
    //         state.items = []
    //         state.status = 'error';
    //     },
    // }
})

export const {setItems} = pizzaSlice.actions   // reducers = action так называется в объекте.

export default pizzaSlice.reducer
