import React, {useEffect, useRef} from 'react';
import qs from "qs"
import {useNavigate} from "react-router-dom";

import '../scss/app.scss'
import Categories from "../components/Categories";
import Sort, {sortList} from "../components/Sort";
import Skeleton from "../components/PizzaBlock/Skeleton";
import PizzaBlock from "../components/PizzaBlock/PizzaBlock";
import Pagination from "../components/Pagination/Pagination";
import {useDispatch, useSelector} from "react-redux";
import {setCategoryId, setCurrentPage, setFilters} from "../redux/slices/filterSlice";
import {fetchPizzas} from "../redux/slices/pizzaSlice";


function Home() {

    const navigate = useNavigate();
    const {categoryId, sort, currentPage, searchValue} = useSelector(
        //@ts-ignore
        state => state.filter);
    const {items, status} = useSelector(//@ts-ignore
        state => state.pizza);

    const dispatch = useDispatch();

    const onChangeCategory = (id: number) => dispatch(setCategoryId(id));
    const onChangePageCount = (num: number) => dispatch(setCurrentPage(num))

    const isSearch = useRef(false);
    const isMounted = useRef(false);

    // –ó–∞–ø—Ä–æ—Å —á–µ—Ä–µ–∑ –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω—ã–π –¥–∏—Å–ø–∞—Ç—á –Ω–∞ —Å–µ—Ä–≤–µ—Ä
    const fetchPizza = async () => {
        const category = (categoryId > 0) ? `category=${categoryId}` : ''; // —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º
        const sortBy = sort.sortProperty.replace('-', ''); // —É–±–∏—Ä–∞–µ–º "-" –∏–∑ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
        const order = sort.sortProperty.includes('-') ? 'asc' : 'desc';  // —É–∫–∞–∑—ã–≤–∞–µ–º —Ç–∏–ø —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏
        const search = (searchValue) ? `&name=${searchValue}` : ''; // –ø–æ–∏—Å–∫ –ø–æ –ø–∏—Ü—Ü–∞–º

        await dispatch(
            //@ts-ignore
            fetchPizzas({currentPage, category, sortBy, order, search}))
    }
    // –£–±–∏—Ä–∞–µ–º –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –≤ URL –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ –ø—Ä–∏ –ø–µ—Ä–≤–æ–º —Ä–µ–Ω–¥–µ—Ä–µ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
    useEffect(() => {
        if (isMounted.current) {
            const queryString = qs.stringify({
                sortProperty: sort.sortProperty, categoryId, currentPage,
            })
            navigate(`?${queryString}`)
        }
        isMounted.current = true;
    }, [categoryId, sort.sortProperty, searchValue, currentPage])

    // –ï—Å–ª–∏ –±—ã–ª –ø–µ—Ä–≤—ã–π —Ä–µ–Ω–¥–µ—Ä, –ø—Ä–æ–≤–µ—Ä—è–µ–º URL-–ø–∞—Ä–∞–º–µ—Ç—Ä—ã –∏ —Å–æ—Ö—Ä–∞–Ω—è–µ–º –≤ redux
    useEffect(() => {
        if (window.location.search) {
            const params = qs.parse(window.location.search.substring(1))
            const sort = sortList.find(item => item.sortProperty === params.sortProperty)
            dispatch(setFilters({...params, sort}));
            isSearch.current = true;
        }
    }, [])

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å–ª–∏ –ª–∏ –≤ –∞–¥—Ä–µ—Å–Ω–æ–π —Å—Ç—Ä–æ–∫–µ –¥–∞–Ω–Ω—ã–µ.
    useEffect(() => {
        fetchPizza();
        isSearch.current = false
    }, [categoryId, sort.sortProperty, searchValue, currentPage])

    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è  –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ –ø–∏—Ü—Ü
    const pizzas = items
        .map((obj: any) => (<PizzaBlock key={obj.id} {...obj}/>))

    const skeletons = [...new Array(4)].map((_, index) => <Skeleton key={index}/>);

    return (<div className="container">
        <div className="content__top">
            <Categories value={categoryId} onClickCategory={(i: number) => onChangeCategory(i)}/>
            <Sort/>
        </div>
        {status === 'error' ? (
                <div className="content__error-info">
                    <div>
                        <span>üòï</span>
                    </div>
                    <h2>–ö —Å–æ–∂–∞–ª–µ–Ω–∏—é, –Ω–µ —É–¥–∞–ª–æ—Å—å –æ—Ç–æ–±—Ä–∞–∑–∏—Ç—å –ø–∏—Ü—Ü—ã.<br/> –ü–æ–≤—Ç–æ—Ä–∏—Ç–µ –ø–æ–ø—ã—Ç–∫—É –ø–æ–∑–∂–µ. </h2>
                </div>
            )
            : (<>
                <h2 className="content__title">–í—Å–µ –ø–∏—Ü—Ü—ã</h2>
                <div className="content__items">
                    {status === 'loading' ? skeletons : pizzas}
                </div>
            </>)}
        <Pagination
            currentPage={currentPage}
            onChangePage={(num: number) => {
                onChangePageCount(num)
            }}/>
    </div>);
}

export default Home;
