import React, {useEffect, useRef} from 'react';
import qs from "qs"
import {useNavigate} from "react-router-dom";

import '../scss/app.scss'
import Categories from "../components/Categories";
import Sort, {sortList} from "../components/Sort";
import Skeleton from "../components/PizzaBlock/Skeleton";
import PizzaBlock from "../components/PizzaBlock/PizzaBlock";
import Pagination from "../components/Pagination/Pagination";
import {useDispatch, useSelector} from "react-redux";
import {selectFilter, setCategoryId, setCurrentPage, setFilters} from "../redux/slices/filterSlice";
import {fetchPizzas} from "../redux/slices/pizzaSlice";


function Home() {

    const navigate = useNavigate();
    const {categoryId, sort, currentPage, searchValue} = useSelector(selectFilter);
    const {items, status} = useSelector(//@ts-ignore
        state => state.pizza);

    const dispatch = useDispatch();

    const onChangeCategory = (id: number) => dispatch(setCategoryId(id));
    const onChangePageCount = (num: number) => dispatch(setCurrentPage(num))

    const isSearch = useRef(false);
    const isMounted = useRef(false);

    // Запрос через асинхронный диспатч на сервер
    const fetchPizza = async () => {
        const category = (categoryId > 0) ? `category=${categoryId}` : ''; // сортировка по категориям
        const sortBy = sort.sortProperty.replace('-', ''); // убираем "-" из параметров
        const order = sort.sortProperty.includes('-') ? 'asc' : 'desc';  // указываем тип фильтрации
        const search = (searchValue) ? `&name=${searchValue}` : ''; // поиск по пиццам

        await dispatch(
            //@ts-ignore
            fetchPizzas({currentPage, category, sortBy, order, search}))
    }
    // Убираем отображение в URL параметров при первом рендере страницы
    useEffect(() => {
        if (isMounted.current) {
            const queryString = qs.stringify({
                sortProperty: sort.sortProperty, categoryId, currentPage,
            })
            navigate(`?${queryString}`)
        }
        isMounted.current = true;
    }, [categoryId, sort.sortProperty, searchValue, currentPage])

    // Если был первый рендер, проверяем URL-параметры и сохраняем в redux
    useEffect(() => {
        if (window.location.search) {
            const params = qs.parse(window.location.search.substring(1))
            const sort = sortList.find(item => item.sortProperty === params.sortProperty)
            dispatch(setFilters({...params, sort}));
            isSearch.current = true;
        }
    }, [])

    // Проверяем, если ли в адресной строке данные.
    useEffect(() => {
        fetchPizza();
        isSearch.current = false
    }, [categoryId, sort.sortProperty, searchValue, currentPage])

    // Генерация  компонентов пицц
    const pizzas = items
        .map((obj: any) => (<PizzaBlock key={obj.id} {...obj}/>))

    const skeletons = [...new Array(4)].map((_, index) => <Skeleton key={index}/>);

    return (<div className="container">
        <div className="content__top">
            <Categories value={categoryId} onClickCategory={(i: number) => onChangeCategory(i)}/>
            <Sort/>
        </div>
        {status === 'error' ? (
                <div className="content__error-info">
                    <div>
                        <span>😕</span>
                    </div>
                    <h2>К сожалению, не удалось отобразить пиццы.<br/> Повторите попытку позже. </h2>
                </div>
            )
            : (<>
                <h2 className="content__title">Все пиццы</h2>
                <div className="content__items">
                    {status === 'loading' ? skeletons : pizzas}
                </div>
            </>)}
        <Pagination
            currentPage={currentPage}
            onChangePage={(num: number) => {
                onChangePageCount(num)
            }}/>
    </div>);
}

export default Home;
