import {createSlice, PayloadAction} from "@reduxjs/toolkit";
import {RootState} from "../store";

export type CartItem = {
    id: number;
    name: string;
    price: number;
    imageUrl: string;
    type: string;
    size: number;
    count: number;
}

type CartActions = {
    id:number;
    size:number;
    type:string;
}

interface CartSliceState {
    totalPrice: number;
    items: CartItem[]
}

const initialState: CartSliceState = {
    totalPrice: 0,
    items: [],
}

const getSum = (items: any) => {
    const totalPrice = items.reduce((sum: number, obj: any) => {
        return sum + (obj.price * obj.count)
    }, 0)
    return totalPrice;
}

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        addItem(state, action: PayloadAction<CartItem>) {
            const findItem = state.items.find((obj) => {
                return (obj.id === action.payload.id && obj.size === action.payload.size && obj.type === action.payload.type)
            });
            (findItem) ? findItem.count++ : state.items.push({...action.payload, count: 1});

            state.totalPrice = getSum(state.items)
        },
        minusItem(state, action:PayloadAction<CartActions>) {
            const findItem = state.items.find(obj =>
                (obj.id === action.payload.id && obj.size === action.payload.size && obj.type === action.payload.type));

            if (findItem && findItem.count > 0) {
                findItem.count--;
            }

            if (findItem?.count === 0) {
                state.items = state.items.filter((obj) => {
                    return !(obj.type === action.payload.type && obj.size === action.payload.size && obj.id === action.payload.id)
                })
            }

            state.totalPrice = getSum(state.items);
        },
        plusItem(state, action:PayloadAction<CartActions>) {
            const findItem = state.items.find(obj => ((obj.id === action.payload.id && obj.size === action.payload.size && obj.type === action.payload.type)));
            if (findItem) {
                findItem.count++;
            }
            state.totalPrice = getSum(state.items);
        }
        ,
        removeItem(state, action:PayloadAction<CartActions>) {
            state.items = state.items.filter(obj => {
                return !(obj.type === action.payload.type && obj.size === action.payload.size && obj.id === action.payload.id);
            });
            state.totalPrice = getSum(state.items);
        }
        ,
        clearItems(state) {
            state.items = [];
            state.totalPrice = getSum(state.items)
        }
    }
})

export const selectCart = (state: RootState) => state.cart;

export const {addItem, removeItem, clearItems, minusItem, plusItem} = cartSlice.actions   // reducers = action так называется в объекте.

export default cartSlice.reducer
