import React, {useCallback, useRef, useState} from 'react';
import debounce from "lodash.debounce"
import styles from './Search.module.scss'
import {useDispatch} from "react-redux";
import {setSearchValue} from "../../redux/slices/filterSlice";


function Search() {
  const dispatch = useDispatch()
  const [value, setValue] = useState('');
  const inputRef = useRef<HTMLInputElement>(null)

  const onClickClear = () => {
    dispatch(setSearchValue(''))
    setValue("");
    inputRef.current?.focus();

  }

  const onChangeInput = (event:any) => {
    setValue(event.target.value);
    updateSearchValue(event.target.value);
  }

  //  функция для обращения к серверу спустя время
  const updateSearchValue = useCallback(
    debounce((value) => {
      dispatch(setSearchValue(value));
    }, 250),
    [],
  )

  return (
    <div className={styles.root}>
      <svg
        className={styles.icon}
        enableBackground="new 0 0 50 50"
        height="50px"
        id="Layer_1"
        version="1.1"
        viewBox="0 0 50 50"
        width="50px"
        xmlns="http://www.w3.org/2000/svg">
        <rect fill="none" height="50" width="50"/>
        <circle cx="21" cy="20" fill="none" r="16" stroke="#000000" strokeLinecap="round" strokeMiterlimit="10"
                strokeWidth="2"/>
        <line fill="none" stroke="#000000" strokeMiterlimit="10" strokeWidth="4" x1="32.229" x2="45.5" y1="32.229"
              y2="45.5"/>
      </svg>
      <input
        ref={inputRef}
        className={styles.input}
        value={value}
        placeholder="Поиск пиццы..."
        onChange={onChangeInput}/>
      {value && (     // добавляем иконку удаления только при наличии текста
        <svg
          onClick={() => {
            onClickClear()
          }}
          className={styles.clearIcon}
          viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <g>
            <path d="M0 0h24v24H0z" fill="none"/>
            <path
              d="M12 10.586l4.95-4.95 1.414 1.414-4.95 4.95 4.95 4.95-1.414 1.414-4.95-4.95-4.95 4.95-1.414-1.414 4.95-4.95-4.95-4.95L7.05 5.636z"/>
          </g>
        </svg>
      )}
    </div>
  )
    ;
}

export default Search;
