import {createAsyncThunk, createSlice} from "@reduxjs/toolkit";
import axios from "axios";

type Pizza = {
    id: number,
    name: string,
    imageUrl: string;
    price: number;
    sizes: number[];
    types: number[];
}

interface PizzaSliceState {
    status: "loading" | "success" | "error";
    items: Pizza[];
}

const initialState: PizzaSliceState = {
    items: [],
    status: 'loading' // loading | success | error
}

export const fetchPizzas = createAsyncThunk("pizza/fetchPizzas", async (params, thunkAPI) => {
    const {currentPage, category, sortBy, order, search} = params;
    const {data} = await axios.get(`https://63bbda34cf99234bfa660b9e.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`);

    if (data.length === 0) {
        throw new Error('rejected')
    }
    return data;
})


const pizzaSlice = createSlice({
    name: 'pizza',
    initialState,
    reducers: {
        setItems(state, action) {
            state.items = action.payload;
        }
    },
    extraReducers: {
        [fetchPizzas.fulfilled]: (state, action) => {
            state.items = action.payload;
            state.status = 'success';
        },
        [fetchPizzas.pending]: (state, action) => {
            state.status = 'loading';
            state.items = [];
        }, [fetchPizzas.rejected]: (state, action) => {
            state.items = []
            state.status = 'error';
        },
    }
})

export const {setItems} = pizzaSlice.actions   // reducers = action так называется в объекте.

export default pizzaSlice.reducer
